mapper.select:
select * from board join board_type on bo_bt_num = bt_num where (bo_title like concat('%',#{cri.search} ,'%') or bo_content like concat('%',#{cri.search} ,'%'))
and bo_bt_num = #{cri.type}
order by bo_ori_num desc, bo_num asc limit #{cri.pageStart}, #{cri.perPageNum}

select * from board 
	join board_type on bo_bt_num = bt_num
	where bo_num = #{bo_num}

mapper.update:
update likes
	set
		li_state = #{li.li_state}
	where
		li_num = #{li.li_num}
