mapper.select:
select * from board 
	where (bo_title like concat('%',#{cri.search},'%')
		or bo_content like concat('%',#{cri.search},'%')
	)

		and bo_bt_num = #{cri.type}

	order by bo_num desc
	limit #{cri.pageStart}, #{cri.perPageNum}

select count(*) from board
	where (bo_title like concat('%',#{cri.search},'%')
		or bo_content like concat('%',#{cri.search},'%')
	)

		and bo_bt_num = #{cri.type}
